import React, {useState, useEffect} from 'react';
import {View, Text, Dimensions, TextInput, Image, StyleSheet, Button, ScrollView} from 'react-native';

const CommentSection = (props) => {

    const [text, onChangeText] = React.useState("");
    const [comments, addComment] = React.useState([]);
    const [new_comment, setComment] = React.useState();

    function handleComment(){
        const newlist = comments.concat({'item': text})
        addComment(newlist);
    }

    return (
        <View style ={{flex: 1}}>
            <View style ={{
                flex: 1
            }}>
                <ScrollView>
                {comments.map((comment, id) => {
                    return <View>
                        <Text key = {id} style={{
                        borderWidth: 4,
                        borderRadius: 20,
                        }}>
                            {comment.item}
                        </Text>
                    </View>
                })}
                </ScrollView>
                
            </View>
            <View style ={{
                justifyContent: 'flex-end',
                marginBottom: 36
            }}>
                <TextInput
                style={styles.input}
                onChangeText={onChangeText}
                value={text}
                placeholder="What do you want to know?"
                />
                <Button
                onPress={handleComment}
                title="Post"
                color="#841584"
                accessibilityLabel="Learn more about this purple button"
                />
            </View>
    </View>
        )
}

const styles = StyleSheet.create({
    input: {
      height: 40,
      margin: 12,
      borderWidth: 1,
      padding: 10,
    },
  });

export default CommentSection;
