import React, {useState, useEffect} from 'react';
import {View, Text, Dimensions, TextInput, Image, StyleSheet, Button, ScrollView, FlatList} from 'react-native';
import uuid from 'react-native-uuid'

const DATA = [
    {
      id: 'bd7acbea-c1b1-46c2-aed5-3ad53abb28ba',
      title: 'First Item',
    },
    {
      id: '3ac68afc-c605-48d3-a4f8-fbd91aa97f63',
      title: 'Second Item',
    },
    {
      id: '58694a0f-3da1-471f-bd96-145571e29d72',
      title: 'Third Item',
    },
  ];

  const Item = ({ title }) => (
    <View style={styles.item}>
      <Text style={styles.title}>{title}</Text>
    </View>
  );

const CommentSection = (props) => {

    const [text, onChangeText] = React.useState("");
    const [comments, addComment] = React.useState([]);

    function handleComment(){
        const newlist = comments.concat({'item': text, 'idx': uuid.v4()})
        addComment(newlist);
    }

    const renderItem = ({ item }) => (
        <View style={{
            borderWidth: 4,
            borderRadius: 20,
            }}>
            <Text style ={{
                marginHorizontal: 5
            }}>
                {item.item}
            </Text>
        </View>
    )

    return (
        <View style ={{flex: 1}}>
            <View style ={{
                flex: 1
            }}>
                <FlatList
                    data={comments}
                    renderItem={renderItem}
                    keyExtractor={comment => comment.idx}
                />
                
            </View>
            <View style ={{
                justifyContent: 'flex-end',
                marginBottom: 36
            }}>
                <TextInput
                style={styles.input}
                onChangeText={onChangeText}
                value={text}
                placeholder="What do you want to know?"
                />
                <Button
                onPress={handleComment}
                title="Post"
                color="#841584"
                accessibilityLabel="Learn more about this purple button"
                />
            </View>
    </View>
        )
}

const styles = StyleSheet.create({
    input: {
      height: 40,
      margin: 12,
      borderWidth: 1,
      padding: 10,
    },
  });

export default CommentSection;
